// 点击 余额抵扣 （选中｜取消选中）
function balanceDeductionCheckChange(deductionInput, payMethodRadio, payMethodId, product_payment_discount_code, payment_discount_code) {
    var $deductionInput = $(deductionInput);
    if ($deductionInput.prop("checked")) {
        // 选中余额抵扣 向服务端获取新的支付信息
        // $('#payment_func li.use_credits').append(renderBalanceDeduction('', true, clientData.balanceDeductCredits));
        $('#payment_func li.use_credits').replaceWith(renderBalanceDeduction('', true, clientData.balanceDeductCredits));
        $.getJSON('api/rest-amount', {
            trade_id: clientData.trade_id,
            pay_amount: clientData.tradePayAmount - clientData.balanceDeductAmount,
            pay_method_id: payMethodId,
            product_payment_discount_code: product_payment_discount_code,
            payment_discount_code: payment_discount_code
        }, function (data) {
            if (data.code === 0){
                var payMethod = data['pay_method'];
                var paymentInfo = payMethod.payment_infos[0];
                payMethodRadioSelected(payMethodRadio,payMethod,paymentInfo,true);
            }else {
                data.message && popup_alert(data.message);
                payMethodRadioSelected(payMethodRadio);
            }
        });
    } else {
        // 取消选中：重新渲染支付信息
        payMethodRadioSelected(payMethodRadio);
    }
}

