            currency_decimals
        )
    );
    return [
        '<li>',
            '<div class="T">'+totalPay+'</div>',
            '<div class="C"><span class="pri" id="totalPrice">'+(isSCredits ? 'Credits' : clientData.currency)+' <b>'+payTotalAmount+'</b></span></div>',
        '</li>'
    ].join("\n");
}

