function renderBalancePayPassword(isCredits) {
    return isCredits ? [
        '<li class="psw">',
            '<div class="F">',
                '<label class="text_wrp"><input id="login_psw" type="password" name="password" autocomplete="off" value="" placeholder="'+yourPassword+'"></label>',
