var ws = new WebSocket(clientData.websocket.host + '?client=js&ver=1.0&sess_id=' + clientData.sessIDEnc);

ws.onopen = function () {
    //safeConsoleLog('websocket opened');
    setInterval(function(){
        var m = JSON.stringify({
            Sender: clientData.sessIDEnc,
            Receiver: clientData.sessIDEnc,
            Command: 'keepalive',
            Data: {time: Date.parse(new Date()) / 1000}
        });
        ws.send(m);
    }, 10 * 1000);
};

ws.onerror = function () {
    //safeConsoleLog('websocket error');
};

ws.onclose = function () {
    //safeConsoleLog('websocket closed');
};

ws.onmessage = function (e) {
    var r = eval("(" + e.data + ")");
    var data = r.Data;
    switch (r.Command) {
        case 'alert':
            alert(data);
            break;
        case 'notification':
            break;
        case 'update_user_balance':
            $(data.selector).html(data.html);
            break;
        case 'topup_complete':
            $(data.selector).html(data.html);
            break;
        case 'refresh_balance':
            $('b.user-blance').html(moneyFormat(data.balance));
            break;
