    var payTotalAmount = isSCredits ? moneyFormat(tradePayAmountBase * 100, 0) : (
        isKCredits ? moneyFormat(tradePayAmount, currency_decimals) : moneyFormat(
            (paymentInfo.fee_type == EM_Type_Discount || paymentInfo.fee_type == EM_Type_Fee_Free_Discount ? paymentInfo.disc_pay_total_amount : (paymentInfo.fee_type == EM_Type_Fee_Free ? paymentInfo.free_pay_total_amount : paymentInfo.norm_pay_total_amount)),
            currency_decimals
        )
    );
